		<div class="title"><h4>Recent Posts</h4>  </div>
        <ul>
            
                <li *ngFor="let item of filteredBlogs">
                    
                        <a class="btn-simple" (click)="goToDetail(item)">{{ item.attributes.Title }}</a>
            
                </li>
         
              
          </ul>

		</div>
			

